module.exports = function(client) {
  client.activities = {};

  /**
   * Export the activities for a course as a CSV file
   *
   * @param  {Course}         course                          The Canvas course in which the user is interacting with the API
   * @param  {Function}       callback                        Standard callback function
   * @param  {Object}         callback.err                    An error that occurred, if any
   * @param  {Object}         callback.body                   The JSON response from the REST API
   * @param  {Response}       callback.response               The response object as returned by requestjs
   * @see col-activities/lib/rest.js for more information
   */
  client.activities.exportActivities = function(course, callback) {
    var requestUrl = client.util.apiPrefix(course) + '/activities.csv';
    client.request(requestUrl, 'GET', null, null, callback);
  };

  /**
   * Get the activity type configration for a course
   *
   * @param  {Course}         course                          The Canvas course in which the user is interacting with the API
   * @param  {Function}       callback                        Standard callback function
   * @param  {Object}         callback.err                    An error that occurred, if any
   * @param  {Object}         callback.body                   The JSON response from the REST API
   * @param  {Response}       callback.response               The response object as returned by requestjs
   * @see col-activities/lib/rest.js for more information
   */
  client.activities.getActivityTypeConfiguration = function(course, callback) {
    var requestUrl = client.util.apiPrefix(course) + '/activities/configuration';
    client.request(requestUrl, 'GET', null, null, callback);
  };

  /**
   * Edit the configuration for an activity type in a course
   *
   * @param  {Course}         course                          The Canvas course in which the user is interacting with the API
   * @param  {String}         activityType                    The activity type for which the configuration should be updated. One of the types in `col-activities/lib/constants.js`
   * @param  {Number}         [points]                        The number of points this activity type should contribute towards a user's points
   * @param  {Boolean}        [enabled]                       Whether activities of this type should contributed towards a user's points
   * @param  {Function}       callback                        Standard callback function
   * @param  {Object}         callback.err                    An error that occurred, if any
   * @param  {Object}         callback.body                   The JSON response from the REST API
   * @param  {Response}       callback.response               The response object as returned by requestjs
   * @see col-activities/lib/rest.js for more information
   */
  client.activities.editActivityTypeConfiguration = function(course, activityType, points, enabled, callback) {
    var requestUrl = client.util.apiPrefix(course) + '/activities/configuration/' + client.util.encodeURIComponent(activityType);
    var data = {
      'points': points,
      'enabled': enabled
    };
    client.request(requestUrl, 'POST', data, null, callback);
  };
};
